package forloops;

public class evenOut {

	public static void main(String[] args) {
		 //TODO Auto-generated method stub
        for (int i = 1;i<=100;i++) {
        	if (i%2==0) {
        		System.out.println("Number is even : " +i);
        		
        	}
        }
        	System.out.println("*************");
        	int num1 = 47;
        	if (num1%2==0) {
        		System.out.println(num1+" is Even Number");
        	}
        	else { 
        		System.out.println(num1+ " is odd Number");
        		
        	}
        }

}


