public class ULalphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         char i;
         System.out.println("Uppercase Alphabets" );
		for (i = 'A'; i<='Z';i++) {
			 System.out.println(i);
			 
		}
		System.out.println("Lowercase Alphabets");
		for (i = 'a'; i<='z';i++ ) {
			System.out.println(i);
		}
		
	
 }
	}


